// =====================================================
// PARSE INTENT OUTPUT (After Basic LLM Chain) - FIXED v2
// =====================================================
// Extracts intent from AI response + keeps all data
// =====================================================

// Get AI output
const aiOutput = ($json.text || $json.response || $json.output || "").toUpperCase().trim();

// Get previous data from Merge node
let previousData = {};
try {
    previousData = $('Merge').first()?.json || {};
} catch (e) {
    try {
        previousData = $('extract structured data from text').first()?.json ||
            $('extract structured data from voice note').first()?.json ||
            {};
    } catch (e2) {
        previousData = {};
    }
}

// =====================================================
// SMART INTENT EXTRACTION (handles verbose AI output)
// =====================================================

let finalIntent = 'CHAT'; // Default
const validIntents = ['ORDER', 'CANCEL', 'MODIFY', 'STATUS', 'COMPLAINT', 'CHAT'];

// Method 1: Look for the LAST occurrence of a valid intent as a standalone word
// This handles "...IS: **CANCEL**" patterns
for (const intent of validIntents) {
    // Look for intent with ** markers or standalone at end
    const patterns = [
        new RegExp(`\\*\\*${intent}\\*\\*`, 'g'),           // **CANCEL**
        new RegExp(`IS[:\\s]*\\*\\*${intent}\\*\\*`, 'g'),  // IS: **CANCEL**
        new RegExp(`IS[:\\s]*${intent}[\\s\\*]*$`, 'g'),    // IS: CANCEL or IS CANCEL at end
        new RegExp(`RESULT[:\\s]*\\*\\*${intent}\\*\\*`, 'g'), // RESULT: **CANCEL**
    ];

    for (const pattern of patterns) {
        if (aiOutput.match(pattern)) {
            finalIntent = intent;
            break;
        }
    }
    if (finalIntent !== 'CHAT') break;
}

// Method 2: If still CHAT, look for Classification/Intent patterns
if (finalIntent === 'CHAT') {
    const patterns = [
        /CLASSIFICATION[:\s]+\*{0,2}(ORDER|CANCEL|MODIFY|STATUS|COMPLAINT|CHAT)\*{0,2}/,
        /INTENT[:\s]+\*{0,2}(ORDER|CANCEL|MODIFY|STATUS|COMPLAINT|CHAT)\*{0,2}/,
        /CATEGORY[:\s]+\*{0,2}(ORDER|CANCEL|MODIFY|STATUS|COMPLAINT|CHAT)\*{0,2}/
    ];

    for (const pattern of patterns) {
        const match = aiOutput.match(pattern);
        if (match) {
            finalIntent = match[1];
            break;
        }
    }
}

// Method 3: If AI just returned a single word, use it directly
if (finalIntent === 'CHAT') {
    const words = aiOutput.split(/\s+/);
    if (words.length === 1) {
        const singleWord = words[0].replace(/[^A-Z]/g, '');
        if (validIntents.includes(singleWord)) {
            finalIntent = singleWord;
        }
    }
}

// Method 4: Simple word search - find LAST valid intent word in output
// This catch-all finds **CANCEL** at the end even with complex formatting
if (finalIntent === 'CHAT') {
    // Find all valid intents mentioned
    const mentionedIntents = [];
    for (const intent of validIntents) {
        // Check for **INTENT** pattern (the actual output format)
        if (aiOutput.includes(`**${intent}**`) || aiOutput.endsWith(intent)) {
            mentionedIntents.push(intent);
        }
    }
    // Use the last mentioned one (usually the conclusion)
    if (mentionedIntents.length > 0) {
        finalIntent = mentionedIntents[mentionedIntents.length - 1];
    }
}

// Method 5: Check customer's original message for unique keywords
const customerMsg = (previousData.customer_message || previousData.message_text || "").toUpperCase().trim();
if (customerMsg === 'DONE') {
    finalIntent = 'ORDER';
} else if (customerMsg === 'XCANCEL') {
    finalIntent = 'CANCEL';
} else if (customerMsg === 'MSAVE') {
    finalIntent = 'MODIFY';
}

// Method 6: If message contains ORD- pattern, it's CANCEL (order ID)
if (customerMsg.match(/^ORD-\d+$/)) {
    finalIntent = 'CANCEL';
}

// Return: All previous data + final intent
return [{
    json: {
        // Customer data
        customer_phone: previousData.customer_phone || null,
        to_number: previousData.to_number || null,
        customer_message: previousData.customer_message || previousData.message_text || null,
        message_text: previousData.message_text || previousData.customer_message || null,
        profile_name: previousData.profile_name || null,
        wa_id: previousData.wa_id || null,
        message_type: previousData.message_type || 'text',
        message_sid: previousData.message_sid || null,
        is_voice_note: previousData.is_voice_note || false,

        // Intent detection result
        final_intent: finalIntent,
        ai_raw_output: aiOutput,

        // Debug
        detection_method: 'ai_llm'
    }
}];

