const kitchenEmail = shopConfig.Kitchen_Email || '';
const managerPhone = shopConfig.Manager_Phone || '';
const shopStatus = shopConfig.Shop_Status || '';
const openingTime = shopConfig.Opening_Time || '';
const closingTime = shopConfig.Closing_Time || '';
const deliveryFee = parseInt(shopConfig.Delivery_Fee) || 0;
const minOrder = parseInt(shopConfig.Min_Order_Amount) || 0;

// Time check
