let noOrder = false;
let orderIdToCancel = null;
let orderRowNumber = null;
let cancelReason = null;

// Check for [[CANCEL_ORDER]]...[[/CANCEL_ORDER]] marker (from AI output)
const cancelOrderMatch = aiOutput.match(/\[\[CANCEL_ORDER\]\]([\s\S]*?)\[\[\/CANCEL_ORDER\]\]/i);
if (cancelOrderMatch) {
    cancelConfirmed = true;
    const content = cancelOrderMatch[1];

    const orderIdMatch = content.match(/Order_ID:\s*(.+)/i);
    const reasonMatch = content.match(/Reason:\s*(.+)/i);

    if (orderIdMatch) {
        orderIdToCancel = orderIdMatch[1].trim();
    }
    if (reasonMatch) {
        cancelReason = reasonMatch[1].trim();
    }
}

// Also check [[CANCEL_CONFIRMED:ORDER_ID]] format (alternative)
const confirmMatch = aiOutput.match(/\[\[CANCEL_CONFIRMED:([^\]]+)\]\]/i);
if (confirmMatch) {
    cancelConfirmed = true;
    orderIdToCancel = confirmMatch[1].trim();
}

// Check [[CANCEL_REJECTED]]
if (aiOutput.includes('[[CANCEL_REJECTED]]')) {
    cancelRejected = true;
}

// Check [[NO_ORDER]]
if (aiOutput.includes('[[NO_ORDER]]')) {
    noOrder = true;
}

// Find the order row number from cancellable orders
if (orderIdToCancel && previousData.cancellable_orders) {
    // Check if it's a number (customer said "1" or "2")
    if (/^\d+$/.test(orderIdToCancel)) {
        const index = parseInt(orderIdToCancel) - 1;
        if (previousData.cancellable_orders[index]) {
            orderRowNumber = previousData.cancellable_orders[index].row_number;
            orderIdToCancel = previousData.cancellable_orders[index].order_id;
        }
    } else {
        // It's an Order ID - search for it
        const order = previousData.cancellable_orders.find(o =>
            o.order_id && o.order_id.toUpperCase() === orderIdToCancel.toUpperCase()
        );
        if (order) {
            orderRowNumber = order.row_number;
        }

        // Also check ROW- format
        if (!orderRowNumber && orderIdToCancel.startsWith('ROW-')) {
            const rowNum = parseInt(orderIdToCancel.replace('ROW-', ''));
            if (rowNum) {
                orderRowNumber = rowNum;
            }
        }

        // Also check ORD- format
        if (!orderRowNumber && orderIdToCancel.startsWith('ORD-')) {
            const order = previousData.cancellable_orders.find(o =>
                o.order_id && o.order_id.includes(orderIdToCancel)
            );
            if (order) {
                orderRowNumber = order.row_number;
            }
        }
    }
}

// Get order details for notification
let orderDetails = null;
if (orderIdToCancel && previousData.cancellable_orders) {
    orderDetails = previousData.cancellable_orders.find(o =>
        o.order_id && o.order_id.toUpperCase() === orderIdToCancel.toUpperCase()
    );
}

// Clean output (remove all markers for customer message)
let cleanOutput = aiOutput
    .replace(/\[\[CANCEL_ORDER\]\][\s\S]*?\[\[\/CANCEL_ORDER\]\]/gi, '')
    .replace(/\[\[CANCEL_CONFIRMED:[^\]]+\]\]/gi, '')
    .replace(/\[\[CANCEL_REJECTED\]\]/gi, '')
    .replace(/\[\[NO_ORDER\]\]/gi, '')
    .trim();

// Clean phone numbers (remove whatsapp: prefix)
const cleanPhone = (phone) => {
    if (!phone) return '';
    return phone.replace('whatsapp:', '');
};

return [{
    json: {
        // Clean AI output for customer
        output: cleanOutput,

        // Cancel detection results
        cancel_confirmed: cancelConfirmed,
        cancel_rejected: cancelRejected,
        no_order: noOrder,

        // Order details
        order_id: orderIdToCancel,
        order_row_number: orderRowNumber,
        order_details: orderDetails,
        cancel_reason: cancelReason,

        // Pass through data (with whatsapp: prefix removed)
        customer_phone: cleanPhone(previousData.customer_phone),
        to_number: cleanPhone(previousData.to_number),
        restaurant_name: previousData.restaurant_name || '',
        kitchen_email: previousData.kitchen_email || ''
    }
}];

