// Parse AI Agent complaint output
const output = $json.output || '';

// Extract data between markers
const match = output.match(/\[\[COMPLAINT_DATA\]\]([\s\S]*?)\[\[END_COMPLAINT_DATA\]\]/);

if (!match) {
    return [{
        json: {
            parsed: false,
            raw_output: output
        }
    }];
}

const dataText = match[1];

// Parse each field
function extractField(text, fieldName) {
    const regex = new RegExp(`${fieldName}:\\s*(.+?)(?:\\n|$)`, 'i');
    const match = text.match(regex);
    return match ? match[1].trim() : null;
}

const parsedData = {
    Ticket_ID: extractField(dataText, 'Ticket_ID'),
    Order_ID: extractField(dataText, 'Order_ID'),
    Items_Ordered: extractField(dataText, 'Items_Ordered'),
    Order_Date: extractField(dataText, 'Order_Date'),
    Order_Time: extractField(dataText, 'Order_Time'),
